<style>
    /* Carousel base class */
    .carousel {
        margin-bottom: 60px;
    }
    /* Since positioning the image, we need to help out the caption */
    .carousel-caption {
        z-index: 10;
    }

    /* Declare heights because of positioning of img element */
    .carousel .item {
        height: 500px;
    }
    .carousel img {
        position: absolute;
        top: 0;
        left: 0;
        min-width: 100%;
        height: 500px;
    }
    /* RESPONSIVE CSS
    -------------------------------------------------- */

    @media (max-width: 768px) {

        .carousel-caption {
            margin-top: -80px;
            z-index: 10;
        }

        /* Bump up size of carousel content */
        .carousel-caption h1 {
            font-size: 17px;
            line-height: 1.4;
        }
        .carousel-caption p {
            font-size: 11px;
            line-height: 1.4;
        }
        .carousel .item {
            height: 300px;
        }
        .carousel img {
            position: absolute;
            top: 0;
            left: 0;
            min-width: 100%;
            height: 300px;
        }


    }
</style>


